<?php/** * Created by PhpStorm. * User: demon * Date: 26.05.2017 * Time: 14:00 */namespace app\components;use app\models\category;use yii\base\Widget;class BookWidget extends Widget {    public $modelBook;    public function init() {        parent::init(); // TODO: Change the autogenerated stub        if ($this->modelBook === null) {            return $this->modelBook = "Ошибка нет записей в базе";        }    }    public function run() {        $modelBook = $this->modelBook;        return $this->render('book', compact('modelBook'));    }    public static function category($idCategory) {        $res = category::getDb()->cache(function($category) use ($idCategory) {            return category::findOne($idCategory);        }, CACH_TIME);        return $res;    }}